Literal.Value:
'{
  "data": [
    {
      "name": "dataset"
    }
  ],
  "signals": [
    {
      "name": "tooltip",
      "value": {},
      "on": [
        {"events": "rect:pointerover", "update": "datum"},
        {"events": "rect:pointerout",  "update": "{}"}
      ]
    }
  ],
  "padding": {"bottom": 0, "top": 0},
  "scales": [
    {
      "name": "xscale",
      "type": "band",
      "domain": {"data": "dataset", "field": "Hour"},
      "range": "width",
      "padding": 0.1,
      "round": false
    },
    {
      "name": "yscale",
      "domain": {"data": "dataset", "field": "Sales Aggregation"},
      "nice": true,
      "range": "height"
    },
    {
      "name": "color",
      "type": "linear",
      "domain": {"data": "dataset", "field": "MaxAbsoluteColorScale"},
      "range": {"scheme": "blues"},
      "reverse": true
    }
  ],

  "axes": [
    { "orient": "bottom", "scale": "xscale" , "labels": false, "ticks": false},
    { "orient": "left", "scale": "yscale" , "tickCount": 4, "labelFontSize": 8, "title": "Sales Agg. By Hour", "titleFontSize": 11}
  ],

  "marks": [
    {
      "type": "rect",
      "from": {"data":"dataset"},
      "encode": {
        "enter": {
          "x": {"scale": "xscale", "field": "Hour"},
          "width": {"scale": "xscale", "band": 1},
          "y": {"scale": "yscale", "field": "Sales Aggregation"},
          "y2": {"scale": "yscale", "value": 0},
          "tooltip": {
              "signal": "{''Hour'': datum[''Hour''], ''Sales Agg.'': format(datum[''Sales Aggregation__highlight''] ?            datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')}"
            },
          "fill": {"scale": "color", "field": "Sales Aggregation"},
          "opacity": {
            "value": 0.3
          }

        }
      }
    },
    {
      "type": "rect",
      "from": {
        "data": "dataset"
      },
      "encode": {
        "enter": {
          "fill": {"scale": "color", "field": "Sales Aggregation"},
          "tooltip": {
            "signal": "{''Hour'': datum[''Hour''], ''Sales Agg.'': format(datum[''Sales Aggregation__highlight''] ?          datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')}"
          },
          "y": {
            "scale": "yscale",
            "field": "Sales Aggregation__highlight"
          },
          "y2": {
            "scale": "yscale",
            "value": 0
          },
          "x": {
            "scale": "xscale",
            "field": "Hour"
          },
          "width": {
            "scale": "xscale",
            "band": 1
          },
          "opacity": [
            {
              "test": "datum.__selected__ == ''off''",
              "value": 0
            }
          ]
        },
        "update": {
            "fill": {"scale": "color", "field": "Sales Aggregation"}
         },
         "hover": {
           "fill": {"value": "cyan"}
         }
      }
    },
    {
      "type": "text",
      "from": {"data": "dataset"},
      "encode": {
        "enter": {
          "text": {"signal": "format(datum[''Sales Aggregation__highlight''] != null ? datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')"},
          "x": {"scale": "xscale", "field": "Hour", "band": 0.5},
          "y": {"scale": "yscale", "field": "Sales Aggregation", "offset":    -3},
          "align": {"value": "center"},
          "baseline": {"value": "bottom"},
          "fontSize": {"value": 8},
          "fill": {"value": "black"}
        }
      }
    }
  ]
}'
