Literal.Value:
'{
  "data": [
    {
      "name": "dataset"
    }
  ],
  "signals": [
    {
      "name": "tooltip",
      "value": {},
      "on": [
        {"events": "rect:pointerover", "update": "datum"},
        {"events": "rect:pointerout",  "update": "{}"}
      ]
    }
  ],
  "padding":{"left": 0, "bottom": 0},
  "scales": [
    {
      "name": "yscale",
      "type": "band",
      "domain": {
        "data": "dataset",
        "field": "BinEnd"
      },
      "range": "height",
      "padding": 0.1,
      "round": false
    },
    {
      "name": "xscale",
      "domain": {
        "data": "dataset",
        "field": "Sales Aggregation"
      },
      "nice": true,
      "range": "width"
    },
    {
      "name": "color",
      "type": "linear",
      "domain": {"data": "dataset", "field": "MaxAbsoluteColorScale"},
      "range": {"scheme": "blues"},
      "reverse": true
    }
  ],
  "axes": [
    {
      "scale": "xscale",
      "orient": "bottom",
      "title": "Sales Agg. by Weather Index Bin",
      "tickCount": 4,
      "labelFontSize": 8,
      "labelFlush": true,
      "titlePadding": 1
    },
    {
      "orient": "left",
      "scale": "yscale",
      "ticks": false,
      "labels": false
    }
  ],
  "marks": [
    {
      "type": "rect",
      "from": {
        "data": "dataset"
      },
      "encode": {
        "enter": {
          "tooltip": {
            "signal": "{''Weather Index Bin'': datum[''BinEnd''], ''Sales Agg.'': format(datum[''Sales Aggregation__highlight''] ? datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')}"
          },
          "x": {
            "scale": "xscale",
            "field": "Sales Aggregation"
          },
          "x2": {
            "scale": "xscale",
            "value": 0
          },
          "y": {
            "scale": "yscale",
            "field": "BinEnd"
          },
          "height": {
            "scale": "yscale",
            "band": 1
          },
          "opacity": {
            "value": 0.3
          },
          "fill": {"scale": "color", "field": "Sales Aggregation"}
        }
      }
    },
    {
      "type": "rect",
      "from": {
        "data": "dataset"
      },
      "encode": {
        "enter": {
          "fill": {"scale": "color", "field": "Sales Aggregation"},
          "tooltip": {
            "signal": "{''Weather Index Bin'': datum[''BinEnd''], ''Sales Agg.'': format(datum[''Sales Aggregation__highlight''] ? datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')}"
          },
          "x": {
            "scale": "xscale",
            "field": "Sales Aggregation__highlight"
          },
          "x2": {
            "scale": "xscale",
            "value": 0
          },
          "y": {
            "scale": "yscale",
            "field": "BinEnd"
          },
          "height": {
            "scale": "yscale",
            "band": 1
          },
          "opacity": [
            {
              "test": "datum.__selected__ == ''off''",
              "value": 0
            }
          ]
        },
        "hover": {
          "fill": {"value": "cyan"}
        },
        "update": {
          "fill": {"scale": "color", "field": "Sales Aggregation"}
        }
      }
    },
    {
      "type": "text",
      "from": {"data": "dataset"},
      "encode": {
        "enter": {
          "text": {"signal": "format(datum[''Sales Aggregation__highlight''] != null ? datum[''Sales Aggregation__highlight''] : datum[''Sales Aggregation''], ''.0f'')"},
          "x": {"scale": "xscale", "field": "Sales Aggregation", "band": 0.5},
          "y": {"scale": "yscale", "field": "BinEnd"},
          "dy": {"value": 12},
          "dx": {"value": 10},
          "align": {"value": "center"},
          "baseline": {"value": "bottom"},
          "fill": {"value": "black"},
          "fontSize": {"value": 8}
        }
      }
    }
  ]
}'
